window.addEventListener("wheel",t=>{t.deltaY<0&&currentSectionIdx>0?scrollToPrev():t.deltaY>0&&currentSectionIdx<sections.length-1&&scrollToNext()});const Section=Object.freeze({Intro:"#intro",About:"#about",Skills:"#skills",Connect:"#connect"});let currentSectionIdx=0,currentTop=0;const sections=[Section.Intro,Section.About,Section.Skills,Section.Connect];function scrollToNext(){isStillScrolling(currentSectionIdx)||currentSectionIdx<sections.length-1&&(currentTop+=getElementY(sections[currentSectionIdx+1]),window.scrollTo(0,currentTop),currentSectionIdx++)}function scrollToPrev(){isStillScrolling(currentSectionIdx)||currentSectionIdx>0&&(currentTop+=getElementY(sections[currentSectionIdx-1]),window.scrollTo(0,currentTop),currentSectionIdx--)}function scrollToSection(t){currentTop+=getElementY(sections[t]),window.scrollTo(0,currentTop)}function isStillScrolling(t){return 0!=getElementY(sections[t])}function getElementY(t){return document.querySelector(t).getBoundingClientRect().top}