window.addEventListener("wheel",e=>{e.deltaY<0&&currentSectionIdx>0?scrollToPrev():e.deltaY>0&&currentSectionIdx<sections.length-1&&scrollToNext()});const Section=Object.freeze({Intro:"#intro",About:"#about",Skills:"#skills",Connect:"#connect"});let currentSectionIdx=0,currentTop=0;const sections=[Section.Intro,Section.About,Section.Skills,Section.Connect];function scrollToNext(){isStillScrolling(currentSectionIdx)||currentSectionIdx<sections.length-1&&(currentTop+=getElementY(sections[currentSectionIdx+1]),window.scrollTo(0,currentTop),currentSectionIdx++)}function scrollToPrev(){isStillScrolling(currentSectionIdx)||currentSectionIdx>0&&(currentTop+=getElementY(sections[currentSectionIdx-1]),window.scrollTo(0,currentTop),currentSectionIdx--)}function scrollToSection(e){currentTop+=getElementY(sections[e]),window.scrollTo(0,currentTop)}function isStillScrolling(e){return 0!=getElementY(sections[e])}function getElementY(e){return document.querySelector(e).getBoundingClientRect().top}const carousel=document.querySelector("#skill-carousel"),nextBtn=document.querySelector("#nav-btn-next"),prevBtn=document.querySelector("#nav-btn-prev"),leftPad=getElementX("#skills");function getElementX(e){return document.querySelector(e).getBoundingClientRect().left}nextBtn.addEventListener("click",e=>{carousel.scrollTo(getElementX("#item2")-leftPad,0)});