const nextBtn=document.querySelector("#nav-btn-next"),prevBtn=document.querySelector("#nav-btn-prev"),peekGradient=document.querySelector("#fade-gradient");window.addEventListener("wheel",e=>{e.deltaY<0&&currentSectionIdx>0?scrollToPrev():e.deltaY>0&&currentSectionIdx<sections.length-1&&scrollToNext()}),peekGradient.addEventListener("click",e=>{scrollToNextItem()}),nextBtn.addEventListener("click",e=>{scrollToNextItem()}),prevBtn.addEventListener("click",e=>{scrollToPrevItem()});const Section=Object.freeze({Intro:"#intro",About:"#about",Skills:"#skills",Connect:"#connect"});let currentSectionIdx=0,currentTop=0;const sections=[Section.Intro,Section.About,Section.Skills,Section.Connect];function scrollToNext(){isStillScrolling(currentSectionIdx)||currentSectionIdx<sections.length-1&&(currentTop+=getElementY(sections[currentSectionIdx+1]),window.scrollTo(0,currentTop),currentSectionIdx++)}function scrollToPrev(){isStillScrolling(currentSectionIdx)||currentSectionIdx>0&&(currentTop+=getElementY(sections[currentSectionIdx-1]),window.scrollTo(0,currentTop),currentSectionIdx--)}function scrollToSection(e){currentTop+=getElementY(sections[e]),window.scrollTo(0,currentTop)}function isStillScrolling(e){return 0!=getElementY(sections[e])}function getElementY(e){return document.querySelector(e).getBoundingClientRect().top}const navDotToggleClass="nav-dot-current";let currentIdx=0;const carousel=document.querySelector("#skill-items-container"),items=Array.from(carousel.getElementsByClassName("skill-item")).slice(0,-1),navDots=Array.from(document.getElementById("nav-dots").getElementsByClassName("nav-dot"));function scrollToNextItem(){currentIdx>=items.length-1||(navDots[currentIdx].classList.remove("nav-dot-current"),currentIdx++,carousel.scrollTo(items[currentIdx].offsetLeft,0),navDots[currentIdx].classList.add("nav-dot-current"))}function scrollToPrevItem(){currentIdx<=0||(navDots[currentIdx].classList.remove("nav-dot-current"),currentIdx--,carousel.scrollTo(items[currentIdx].offsetLeft,0),navDots[currentIdx].classList.add("nav-dot-current"))}function getRelativeX(e){return document.querySelector(e).offsetLeft}