const Section=Object.freeze({Intro:"#intro",About:"#about",Skills:"#skills",Connect:"#connect"});let currentSectionIdx=0,currentTop=0;const sections=[Section.Intro,Section.About,Section.Skills,Section.Connect];function scrollToSection(e){currentTop+=getElementY(sections[e]),window.scrollTo(0,currentTop)}function isStillScrolling(e){return 0!=getElementY(sections[e])}function getElementY(e){return document.querySelector(e).getBoundingClientRect().top}window.addEventListener("wheel",e=>{console.log("From Idx:"+currentSectionIdx),isStillScrolling(currentSectionIdx)||(e.deltaY<0&&currentSectionIdx>0?(scrollToSection(currentSectionIdx-1),currentSectionIdx--,console.log("To Idx:"+currentSectionIdx)):e.deltaY>0&&currentSectionIdx<sections.length-1&&(scrollToSection(currentSectionIdx+1),currentSectionIdx++,console.log("To Idx:"+currentSectionIdx)))});